import { SET_SCREAMS, LIKE_SCREAM, UNLIKE_SCREAM, LOADING_DATA } from "../types"

const initialState = {
  screams: [],
  scream: {},
  loading: false
};

export default function(state = initialState, actio) {
  switch(actio.type) {
    case LOADING_DATA:
      return {
        ...state,
        loading: true
      }
    case SET_SCREAMS:
      return {
        ...state,
        screams: actio.payload
      }
    default:
      return state;
  }
}
