import React, { Component, Fragment } from 'react'
import PropTypes from 'prop-types'
import withStyles from '@material-ui/core/styles/withStyles'
import MyButton from '../util/myButton'

//REDUX
import { connect } from 'react-redux';
import { editUserDetails } from '../redux/actions/userActions'

//MUI
import Tooltip from '@material-ui/core/Tooltip';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';
import IconButton from '@material-ui/core/IconButton';

// Icons
import EditIcon from '@material-ui/icons/Edit'

const styles = (theme) => ({
  ...theme.classes,
  button: {
    float: 'right'
  }
})

class EditDetails extends Component {
  state = {
    bio: '',
    website: '',
    location: '',
    open: false
  };

  mapUserDetailsToState = (credentials) => {
    this.setState({
      bio: credentials.bio ? credentials.bio : '',
      website: credentials.website ? credentials.website : '',
      location: credentials.location ? credentials.location : ''
    });
  }
  handleOpen = () => {
    this.setState({
      open: true
    })
    this.mapUserDetailsToState(this.props.credentials);
  }
  handleClose = () => {
    this.setState({ 
      open: false
    })
  }
  componentDidMount() {
    const { credentials } = this.props;
    this.mapUserDetailsToState(credentials);

  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  handleSubmit = () => {
    const userDetaills = {
      bio: this.state.bio,
      website: this.state.website,
      location: this.state.location
    };
    this.props.editUserDetails(userDetaills);
    this.handleClose();
  }

  render() {
    const { classes } = this.props;
    return (
      <Fragment>
        <Tooltip title="Edit details" placement="top">
          <IconButton onClick={this.handleOpen} className={classes.button}>
            <EditIcon color="primary" />
          </IconButton>
        </Tooltip>
        <MyButton tip="Edit details" onClick={this.handleEditPicture} btnClassName="button">
          <EditIcon color="primary" />
        </MyButton>
        <Dialog 
          open={this.state.open}
          onClose={this.handleClose}
          fullWidth
          maxWidth = "sm">
          <DialogTitle>Edit your details</DialogTitle>
          <DialogContent>
            <form>
              <TextField 
                name="bio"
                type="text"
                label="Bio..."
                variant="outlined"
                multiline
                rows="3"
                placeholder="A short bio about yourself"
                className={classes.textfield}
                value={this.state.bio}
                onChange={this.handleChange}
                fullWidth 
              />

              <TextField 
                name="website"
                type="text"
                label="Website..."
                variant="outlined"
                placeholder="Your personal/professional website"
                className={classes.textfield}
                value={this.state.website}
                onChange={this.handleChange}
                fullWidth 
              />

              <TextField 
                name="location"
                type="text"
                label="Location..."
                variant="outlined"
                placeholder="Your location"
                className={classes.textfield}
                value={this.state.location}
                onChange={this.handleChange}
                fullWidth 
              />
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={this.handleClose} color="primary">Cancel</Button>
            <Button onClick={this.handleSubmit} color="primary">Save</Button>
          </DialogActions>
        </Dialog>
      </Fragment>
    )
  }
}

EditDetails.propTypes = {
  editUserDetails: PropTypes.func.isRequired,
  classes: PropTypes.object.isRequired
}

const mapStateToProps = (state) => ({
  credentials: state.user.credentials
})

const mapActionsToProps = { editUserDetails }


export default connect(mapStateToProps, mapActionsToProps)(withStyles(styles)(EditDetails))
