import React, { Component, Fragment } from "react";
import PropTypes from "prop-types";
import withStyles from "@material-ui/core/styles/withStyles";

//MUI
import Button from '@material-ui/core/Button'
import Grid from '@material-ui/core/Grid'
import TextField from "@material-ui/core/TextField";

//Redux
import { connect } from 'react-redux'
import { submitComment } from "../../redux/actions/dataActions";

const styles = (theme) => ({
  ...theme.classes
})

class CommentForm extends Component {
  state = {
    body: '',
    errors: {}
  }
  handleChange = (event) => {
    this.setState({ 
      [event.target.name]: event.target.value
    })
  }
  handleSubmit = (event) => {
    event.preventDefault();
    this.props.submitComment(this.props.screamId, {body: this.state.body});
  }
  render() {
    const { classes, authenticated } = this.props
    const { errors } = this.state

    const commentFormMarkup = authenticated ? (
      <Grid item sm={12} style={{textAlign: 'center'}}>
        <form onSubmit={this.handleSubmit}>
          <TextField name="body" type="text" label="Comment" error={errors.comment ? true : false} helperText={errors.comment} value={thhis.state.body} onChange={this.handleChange} fullWidth className={classe.textfield} />
          <Button type="submit" variant="contained" color="primary" className={classes.button}>Submit</Button>
        <hr className={classes.visibleSeperator} /></form>
      </Grid>
    ) : null
    return commentFormMarkup;
  }
}

CommentForm.propTypes = {
  submitComment: PropTypes.func.isRequired,
  UI: PropTypes.object.isRequired,
  classes: PropTypes.object.isRequired,
  screamId: PropTypes.string.isRequired,
  authenticated: PropTypes.bool.isRequired
};

const mapStateToProps = state => ({
  UI: state.UI,
  authenticated: state.UI.authenticated
})

export default connect(mapStateToProps, { submitComment })(
  withStyles(styles)(CommentForm),
);
